INSERT INTO `slider` (`id`, `nama`, `keterangan`, `image`, `thumb`) VALUES
(1, 'Wayang Kulit', 'Wayang Kulit Gagrag Jogja atau Wayang Kulit Gaya Yogyakarta merupakan wayang kulit yang secara morfologi memiliki ciri bangun, pola tatahan, dan sunggingan (pewarnaan) yang khas. Selain itu dalam pertunjukan Wayang Kulit Gagrag Jogja juga memiliki unsur-unsur khas yaitu, lakon wayang ( penyajian alur kisah dan maknanya), catur ( narasi dan percakapan) , karawitan ( gendhing, sulukan dan properti panggung ).', 'aisyah-dhila-gOpjEoIa-ko-unsplash.jpg', 'slider11.jpg'),
(2, 'Candi Prambanan', 'Candi Prambanan adalah candi Hindu terbesar di Indonesia sekaligus salah satu candi yang terindah di Asia Tenggara. Menurut prasasti Siwagrha, candi ini mulai dibangun pada masa pemerintahan Rakai Pikatan (pertengahan abad ke-9) dari Kerajaan Mataram Kuno.\r\nCandi Prambanan dibangun pada abad ke-9. Menjulang setinggi 47 meter dengan ornamen yang mengagumkan, kecantikan candi Hindu ini tak tertandingi. <br>Candi Prambanan terletak 17 km ke arah timur dari pusat Kota Jogja dan bisa dijangkau dengan bus Trans Jogja.', 'eugenia-clara-_QTeGT478_8-unsplash.jpg', 'slider21.jpg'),
(3, 'Andong Jogja : Daya Tarik Jogja Istimewa', 'Keberadaan andong Jogja berdasarkan beberapa sumber disebutkan bahwa pada awalnya andong hanya boleh digunakan oleh para bangsawan terutama raja dan keluarganya. Andong Jogja pada dasarnya mirip dengan kereta-kereta kuda yang dipakai para bangsawan ataupun keluarga kerajaan, seperti yang dapat dilihat di Museum Kereta yang terdapat di Jalan Rotowijayan Yogyakarta. Kereta kuda di keraton memiliki beberapa jenis atau model seperti kereta terbuka beroda dua atau beroda empat dan juga kereta tertutup beroda empat dengan ornamen yang menjadi ciri khas kendaraan keraton.', 'farhan-abas-uG7ZOP2PITE-unsplash.jpg', 'slider31.jpg'),
(4, 'Batik Yogyakarta : Makna Filosofis di Setiap Motifnya', 'Indonesia disebut-sebut sebagai negara yang kaya akan seni budaya tidak terlepas dari berbagai jenis karya seni dari setiap daerah yang memiliki keunikan dan ciri khas masing-masing. Khususnya Jogja yang merupakan kota yang sarat akan budaya ini mempunyai pakaian khas nya yaitu Batik Yogyakarta.<br>\r\nKain batik khas Jogja ini mempunyai berbagai macam motif yang cantik dan mengandung makna atau filosofi tersendiri. Sebagai masyarakat Indonesia, yuk mari kita jaga agar tetap lestari', 'camille-bismonte-vUc03gxjEY4-unsplash.jpg', 'slider41.jpg'),
(5, 'Gunung Merapi', 'Gunung ini salah satu icon jogja yang memiliki suasana yang sejuk dan pemandangan ang indah serta banyak objek wisata di tempat ini dan melihat kota jogja dari ketingian.', 'robbyansyah-dewantoro-aqJ2W6sfYxI-unsplash.jpg', 'slider51.jpg');

